mapper.select:
SELECT count(*) FROM (SELECT d.*,e.`name` dealer_name FROM (SELECT c.* FROM (SELECT a.*,b.`channel_name` FROM yq_order a LEFT JOIN channel b ON a.`channel_id`=b.`id` ) c) d LEFT JOIN dealer_info e ON d.dealer_id=e.`id`) f where 1=1
and f.channel_name = #{channelName,jdbcType=VARCHAR}
and f.dealer_id = #{dealerId,jdbcType=VARCHAR}
and f.brand_name = #{brandName,jdbcType=VARCHAR}
and f.vin like concat(concat("%",#{vin,jdbcType=VARCHAR}),"%")
and f.status = #{status,jdbcType=INTEGER}
and f.create_time >= DATE_FORMAT(#{fromTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
and f.create_time <= DATE_FORMAT(#{toTime,jdbcType=TIMESTAMP},'%Y-%m-%d')

SELECT f.* FROM (SELECT d.*,e.`name` dealer_name FROM (SELECT c.* FROM (SELECT a.*,b.`channel_name` FROM yq_order a LEFT JOIN channel b ON a.`channel_id`=b.`id` ) c) d LEFT JOIN dealer_info e ON d.dealer_id=e.`id`) f where 1=1
and f.channel_name = #{channelName,jdbcType=VARCHAR}
and f.dealer_id = #{dealerId,jdbcType=INTEGER}
and f.brand_name = #{brandName,jdbcType=VARCHAR}
and f.vin like concat(concat("%",#{vin,jdbcType=VARCHAR}),"%")
and f.status = #{status,jdbcType=INTEGER}
and f.create_time >= DATE_FORMAT(#{fromTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
and f.create_time <= DATE_FORMAT(#{toTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
order by f.id desc
